import {React, useState} from 'react'
import '../styles/Navbar.css'
import { Link } from 'react-router-dom'
import logo from '../assets/GWC_Final-Logo_Teal.png'; // Adjust the path as needed

const Navbar = () => {const [activeLink, setActiveLink] = useState("");

  const handleClick = (linkName) => {
      setActiveLink(linkName);
  };

  return (
      <div className='navbar'>
          <Link to="/" className="logo">
              <img src={logo} alt="logo_pic" />
          </Link>
          <div className='links'>
              <Link 
                  to="/about" 
                  className={`about ${activeLink === 'about' ? 'clicked' : ''}`}
                  onClick={() => handleClick('about')}
              >About</Link>
              <Link 
                  to="/events" 
                  className={`events ${activeLink === 'events' ? 'clicked' : ''}`}
                  onClick={() => handleClick('events')}
              >Events</Link>
              <Link 
                  to="/people" 
                  className={`people ${activeLink === 'people' ? 'clicked' : ''}`}
                  onClick={() => handleClick('people')}
              >People</Link>
              <Link 
                  to="/resources" 
                  className={`resources ${activeLink === 'resources' ? 'clicked' : ''}`}
                  onClick={() => handleClick('resources')}
              >Resources</Link>
              <Link 
                  to="/contact" 
                  className={`contact ${activeLink === 'contact' ? 'clicked' : ''}`}
                  onClick={() => handleClick('contact')}
              >Contact</Link>
          </div>
      </div>
  )

}

export default Navbar
