import React, { useState, useEffect } from 'react';
import '../styles/Navbar.css';
import { NavLink, Link } from 'react-router-dom';
import logo from '../assets/GWC_Final-Logo_Teal.png';
import mascot from '../assets/mascot.png';
import { GiHamburgerMenu } from "react-icons/gi";
import { AiOutlineClose } from "react-icons/ai";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 1024);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleResize = () => {
    if (window.innerWidth > 1024) {
      setIsOpen(false); // Close the menu if screen size is larger than 1024px
    }
    setIsMobile(window.innerWidth <= 1024);
  };

  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div className={`navbar ${isOpen ? 'active' : ''}`}>
      <div className="left-side">
        <Link to="/">
          <img src={logo} alt="logo_pic" className="logo" />
        </Link>
        <img src={mascot} alt="mascot" className='mascot-img' />
      </div>

      <div className={`links ${isMobile && isOpen ? 'open' : ''}`}>
        <NavLink to="/about" className={({ isActive }) => isActive ? 'active' : ''}>About</NavLink>
        <NavLink to="/events" className={({ isActive }) => isActive ? 'active' : ''}>Events</NavLink>
        <NavLink to="/people" className={({ isActive }) => isActive ? 'active' : ''}>People</NavLink>
        <NavLink to="/resources" className={({ isActive }) => isActive ? 'active' : ''}>Resources</NavLink>
        <NavLink to="/contact" className={({ isActive }) => isActive ? 'active' : ''}>Contact</NavLink>
      </div>

      {isMobile && (
        <div className="hamburger" onClick={toggleMenu}>
          {isOpen ? <AiOutlineClose /> : <GiHamburgerMenu />}
        </div>
      )}
    </div>
  );
};

export default Navbar;
