import React, { useEffect, useState } from 'react';
import { initClient, listUpcomingEvents } from './googleApi';
import '../styles/UpcomingEvents.css';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';


const UpcomingEvents = () => {
  const [events, setEvents] = useState([]);
  const [date, setDate] = useState(new Date());
  const calendarEmbed = `<iframe src="https://calendar.google.com/calendar/embed?src=lolahere09%40gmail.com&ctz=America%2FLos_Angeles" width="800" height="600" frameborder="0" scrolling="no"></iframe>`;



  useEffect(() => {
    initClient().then(() => {
      loadEvents();
    }).catch((error) => {
      console.error("Error loading GAPI client for API", error);
    });
  }, []);

  const loadEvents = () => {
    listUpcomingEvents().then(response => {
      const eventItems = response.result.items.map(event => ({
        id: event.id,
        summary: event.summary,
        start: event.start.dateTime || event.start.date,
      }));
      setEvents(eventItems);
    }).catch((error) => {
      console.error("Error fetching events", error);
    });
  };

  const handleDateChange = (date) => {
    setDate(date);
  };

  return (
    <div>
      <h1>Google Calendar Events</h1>
      <ul>
        {events.map(event => (
          <li key={event.id}>
            {event.summary} ({event.start.dateTime || event.start.date})
            <br /> Location üìç: {event.location} 
            <br /> Description üíÅ‚Äç‚ôÄÔ∏è: {event.description}
          </li>
        ))}
      </ul>

      <div dangerouslySetInnerHTML={{ __html: calendarEmbed }} />

    </div>
  );
};

export default UpcomingEvents;
